import React, {Component} from 'react'
import {Alert, Text, StyleSheet, View, Image, TextInput, TouchableOpacity} from 'react-native'

export default class App extends Component {

  clicou = () => {
    Alert.alert("LOGIN EFETUADO","Login Efetuado!");
  }
  render () {
    return (
      //********************************************* */
        <View style={styles.container}> 
         <Image 
          source={require('./assets/img/logo.png')}
          style={styles.logo}
         />

         <TextInput
          style={styles.input}
          placeholder="Digite seu email:"
         />

         <TextInput
          style={styles.input}
          secureTextEntry={true}
          placeholder="Digite sua senha"
         />  

        <TouchableOpacity
          style={styles.botao}
          onPress= { () => {this.clicou()}}
        >
          <Text style={styles.botaoText}>Login</Text> 
        </TouchableOpacity> 

        </View>     
    )
  }
}
//************************ */
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems:'center',
    backgroundColor: '#2c3e50'
  },
  logo: {
    width: 200,
    height: 200, 
    borderRadius: 200
  },
  input: {
    marginTop: 10,
    padding: 10,
    width: 300,
    backgroundColor: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
    borderRadius: 3
  },
  botao: {
    width: 300,
    height: 42,
    backgroundColor:'#3498db',
    marginTop: 10,
    borderRadius: 4,
    alignItems: 'center',
    justifyContent:'center'
  },
  botaoText: {
    fontSize: 16,
    fontWeight:'bold',
    color:'#fff'
  }
});
